public class ExtendsTest extends MyClass {

	
	public ExtendsTest(int i){	
	}
	public static void main(String[] args) {
		
		ExtendsTest et=new ExtendsTest(8);		
		et.sMethod();
		MyClass bc=(MyClass)et;
		bc.sMethod();
	}
	public static void sMethod(){
		System.out.println("static method in extended class");
	}
}

class MyClass{
	
	public MyClass(){
		System.out.println("MyClass");
	}
	public MyClass(int i){
		System.out.println("MyClass with int input");
	}
	public static void sMethod(){
		System.out.println("static method in base class");
	}
}


